/*------------------------------------------------
            MATCH HEIGHT
------------------------------------------------*/

    $('.single #primary .navigation a').matchHeight();
    $('#mustread .entry-container').matchHeight();
    $('.blog-six .blog-posts-wrapper article').matchHeight();
    $('#recent .under-content article .entry-container').matchHeight();

